import dbClient from '../utils/db';

const sha1 = require('sha1');

const postNew = (res, req) => {
  if (!req.body.email) {
    return res.status(400).send({ error: 'Missing email' });
  }
  if (!req.body.password) {
    return res.status(400).send({ error: 'Missing password' });
  }
  const emailExists = dbClient
    .collection('users')
    .findOne({ email: req.body.email });
  if (emailExists) {
    return res.status(400).send({ error: 'Already exist' });
  }
  const hash = sha1(req.body.password);
  const user = dbClient.db
    .collection('users')
    .insertOne([{ email: req.body.email, password: hash }]);
  return res.status(201).send({ id: user.insertedId, email: req.body.email });
};
module.exports = { postNew };
